package com.example.zhbj47.pager;

import java.util.ArrayList;
import java.util.List;

import android.content.Context;
import android.text.TextUtils;
import android.util.Log;
import android.view.View;
import android.view.View.OnClickListener;
import android.widget.FrameLayout;

import com.example.zhbj47.MainActivity;
import com.example.zhbj47.R;
import com.example.zhbj47.bean.NewCenter;
import com.example.zhbj47.utils.HMApi;
import com.example.zhbj47.utils.SharedPreferencesUtil;
import com.google.gson.Gson;
import com.lidroid.xutils.ViewUtils;
import com.lidroid.xutils.exception.HttpException;
import com.lidroid.xutils.http.ResponseInfo;
import com.lidroid.xutils.http.callback.RequestCallBack;
import com.lidroid.xutils.http.client.HttpRequest.HttpMethod;
import com.lidroid.xutils.view.annotation.ViewInject;

/**
 * 网易新闻中心 页
 * @author rong
 *
 */
public class NewCenterPager extends BasePager {

	protected static final String tag = "NewCenterPager";

	/**
	 * 网易新闻左侧栏的四个按钮标题
	 */
	private List<String> titleList = new ArrayList<String>();

	/**
	 * 网易新闻的四个子界面
	 */
	private List<BasePager> pagerList;

	@ViewInject(R.id.news_center_fl)
	private FrameLayout news_center_fl;

	public NewCenterPager(Context context) {
		super(context);
	}

	@Override
	public View initView() {
		view = View.inflate(context, R.layout.news_center_frame, null);
		ViewUtils.inject(this, view);
		return view;
	}

	@Override
	public void initData() {

		// 判断一下当前本地是否有数据
		String result = SharedPreferencesUtil.getStringData(context,
				HMApi.NEWS_CENTER_CATEGORIES, "");
		if (!TextUtils.isEmpty(result)) {
			// 有值，展示，json解析过程
			processData(result);
		}
		// 请求网络数据，xutil请求网络
		getData();

	}

	/**
	 * 解析json操作
	 */
	private void processData(String result) {
		// json解析
		Gson gson = new Gson();
		NewCenter newCenter = gson.fromJson(result, NewCenter.class);
		Log.i(tag, newCenter.data.get(0).children.get(0).url);

		titleList.clear();
		for (int i = 0; i < newCenter.data.size(); i++) {
			titleList.add(newCenter.data.get(i).title);
		}

		// 当前的titleList需要给左侧侧拉条MenuFragment对象目去使用

		// 获取MenuFragment对象，通过mainactivity得到，然后在调用menuFragment中的方法
		((MainActivity) context).getMenuFragment().initMenu(titleList);

		// 拿到解析的json数据，创建新闻模块的四个子模块
		pagerList = new ArrayList<BasePager>();

		pagerList.clear();
		pagerList.add(new NewPager(context, newCenter.data.get(0)));
		pagerList.add(new TopicPager(context, newCenter.data.get(1)));
		pagerList.add(new PicPager(context, newCenter.data.get(2)));
		pagerList.add(new IntPager(context, newCenter.data.get(3)));

		// 默认加载新闻数据页面，索引为0；
		switchPager(0);

	}

	/**
	 * 切换页面，根据左侧点击的条目
	 * 
	 * @param i
	 */
	public void switchPager(int position) {

		initTitleBar();
		txt_title.setText(titleList.get(position));

		news_center_fl.removeAllViews();
		news_center_fl.addView(pagerList.get(position).getRootView());

		pagerList.get(position).initData();
		
		//如果是组图模块，要处理按钮
		if(position == 2){
			imgbtn_text.setVisibility(View.VISIBLE);
			imgbtn_text.setImageResource(R.drawable.icon_pic_list_type);
			imgbtn_text.setOnClickListener(new OnClickListener() {
				@Override
				public void onClick(View v) {
					//切换对应按钮的显示样式，并且切换组图模块对应的显示样式
					PicPager picPager = (PicPager) pagerList.get(2);
					picPager.switchListViewOrGridView(imgbtn_text);
				}
			});
		}else{
			imgbtn_text.setVisibility(View.GONE);
		}
	}

	/**
	 * 从网络获取数据，存储到本地，调用解析以显示
	 */
	private void getData() {
		requestData(HttpMethod.GET, HMApi.NEWS_CENTER_CATEGORIES, null,
				new RequestCallBack<String>() {
					@Override
					public void onSuccess(ResponseInfo<String> responseInfo) {
						String info = responseInfo.result;
						// System.out.println(info);
						// 请求成功 然后通过sp工具类存储数据
						SharedPreferencesUtil.saveStringData(context,
								HMApi.NEWS_CENTER_CATEGORIES, info);
						// 解析json
						processData(info);
					}

					@Override
					public void onFailure(HttpException error, String msg) {
					}
				});

	}

}
